import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import * as d3 from 'd3';
import './Prediction.css';

const GeneratedRentPrediction = () => {
    const location = useLocation();
    const { suburb, houseType, numRooms, rentalPeriod, predictedPrice } = location.state || {};
    const normalizedSuburb = suburb
        .toLowerCase()
        .split(' ')
        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
        .join(' ');

    const [historicalPrices, setHistoricalPrices] = useState(null);
    const [rentComparisons, setRentComparisons] = useState(null);
    const svgRef = useRef(); // Reference for D3 chart
    const comparisonSvgRef = useRef(); // Reference for D3 comparison chart

    useEffect(() => {
        const fetchHistoricalPrices = async () => {
            try {
                const response = await fetch('http://localhost:8000/get_historical_rent_prices', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        suburb: suburb,
                        numRooms: numRooms,
                        houseType: houseType,
                        monthsBack: 6,
                    }),
                });
                if (!response.ok) {
                    throw new Error("Failed to fetch historical rent prices");
                }
                const data = await response.json();
                setHistoricalPrices(data.historical_data);
            } catch (error) {
                console.error("Error fetching historical rent prices:", error);
            }
        };

        const fetchRentComparisson = async () => {
            try {
                const response = await fetch('http://localhost:8000/get_rent_comparisson', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        suburb: suburb,
                        numRooms: numRooms,
                        houseType: houseType,
                        monthsAhead: rentalPeriod,
                    }),
                });
                if (!response.ok) {
                    throw new Error("Failed to fetch rent comparison data");
                }
                const data = await response.json();
                setRentComparisons(data.predictions);
            } catch (error) {
                console.error("Error fetching rent comparison data:", error);
            }
        };

        fetchHistoricalPrices();
        fetchRentComparisson();
    }, [suburb, numRooms, houseType, rentalPeriod]);

    // Set up D3 chart for historical prices after data is fetched
    useEffect(() => {
        if (historicalPrices) {
            const width = 600;
            const height = 400;
            const margin = { top: 20, right: 30, bottom: 70, left: 60 };

            const parseDate = d3.timeParse('%m-%Y');
            const data = historicalPrices
                .map(d => ({
                    date: parseDate(d.date),
                    price: +d.price,
                }))
                .filter(d => d.date);

            const xScale = d3.scaleTime()
                .domain(d3.extent(data, d => d.date))
                .range([margin.left, width - margin.right]);

            const yScale = d3.scaleLinear()
                .domain([0, d3.max(data, d => d.price)]).nice()
                .range([height - margin.bottom, margin.top]);

            const line = d3.line()
                .x(d => xScale(d.date))
                .y(d => yScale(d.price));

            d3.select(svgRef.current).selectAll('*').remove();

            const svg = d3.select(svgRef.current)
                .attr('width', width)
                .attr('height', height);

                // Add X axis
                svg.append('g')
                    .attr('transform', `translate(0,${height - margin.bottom})`)
                    .call(d3.axisBottom(xScale))
                    .selectAll("text")
                    .attr("transform", "rotate(-45)")
                    .attr("y", 10)
                    .style("text-anchor", "end");
        
                // X-axis label
                svg.append("text")
                    .attr("text-anchor", "middle")
                    .attr("x", width / 2)
                    .attr("y", height - margin.bottom + 70)
                    .attr("fill", "black")
                    .text("Date");
        
                // Add Y axis
                svg.append('g')
                    .attr('transform', `translate(${margin.left},0)`)
                    .call(d3.axisLeft(yScale));
        
                // Y-axis label
                svg.append("text")
                    .attr("text-anchor", "middle")
                    .attr("transform", `rotate(-90)`)
                    .attr("x", -height / 2)
                    .attr("y", margin.left - 40)
                    .attr("fill", "black")
                    .text("Price per week (AUD)");

            svg.append('path')
                .datum(data)
                .attr('fill', 'none')
                .attr('stroke', 'steelblue')
                .attr('stroke-width', 2)
                .attr('d', line);

            svg.selectAll("circle")
                .data(data)
                .join("circle")
                .attr("cx", d => xScale(d.date))
                .attr("cy", d => yScale(d.price))
                .attr("r", 4)
                .attr("fill", "steelblue");
        }
    }, [historicalPrices]);

    // Set up D3 chart for rent comparison data as a column chart with unique colors
    useEffect(() => {
        if (rentComparisons) {
            const width = 800;
            const height = 500;
            const margin = { top: 20, right: 30, bottom: 100, left: 60 };
    
            // Process data: Group by suburb with separate entries for Flat and House
            const groupedData = d3.groups(rentComparisons, d => d.suburb);
    
            // Set up scales
            const xScale = d3.scaleBand()
                .domain(groupedData.map(d => d[0]))  // Domain is each suburb
                .range([margin.left, width - margin.right])
                .padding(0.2);
    
            // Set up a second scale for the housing type within each suburb
            const xSubgroup = d3.scaleBand()
                .domain(["Flat", "House"])  // Define subgroups
                .range([0, xScale.bandwidth()])
                .padding(0.05);
    
            // Y scale for price
            const yScale = d3.scaleLinear()
                .domain([0, d3.max(rentComparisons, d => d.price)]).nice()
                .range([height - margin.bottom, margin.top]);
    
            // Color scale for housing types
            const colorScale = d3.scaleOrdinal()
                .domain(["Flat", "House"])
                .range(["#1f77b4", "#ff7f0e"]);
    
            // Clear any previous content
            d3.select(comparisonSvgRef.current).selectAll('*').remove();
    
            // Create SVG container
            const svg = d3.select(comparisonSvgRef.current)
                .attr('width', width)
                .attr('height', height);
    
            // Add X axis
            svg.append('g')
                .attr('transform', `translate(0,${height - margin.bottom})`)
                .call(d3.axisBottom(xScale))
                .selectAll("text")
                .attr("transform", "rotate(-45)")
                .attr("y", 10)
                .style("text-anchor", "end");
    
            // X-axis label
            svg.append("text")
                .attr("text-anchor", "middle")
                .attr("x", width / 2)
                .attr("y", height - margin.bottom + 80)
                .attr("fill", "black")
                .text("Suburb");
    
            // Add Y axis
            svg.append('g')
                .attr('transform', `translate(${margin.left},0)`)
                .call(d3.axisLeft(yScale));
    
            // Y-axis label
            svg.append("text")
                .attr("text-anchor", "middle")
                .attr("transform", `rotate(-90)`)
                .attr("x", -height / 2)
                .attr("y", margin.left - 40)
                .attr("fill", "black")
                .text("Price per week (AUD)");
    
            // Draw bars for each housing type within each suburb group
            svg.append("g")
                .selectAll("g")
                .data(groupedData)
                .join("g")
                .attr("transform", d => `translate(${xScale(d[0])},0)`)
                .selectAll("rect")
                .data(d => d[1])  // Access individual items within each suburb group
                .join("rect")
                .attr("x", d => xSubgroup(d.housing_type))
                .attr("y", d => yScale(d.price))
                .attr("width", xSubgroup.bandwidth())
                .attr("height", d => height - margin.bottom - yScale(d.price))
                .attr("fill", d => colorScale(d.housing_type));
    
            // Legend for housing types
            const legend = svg.append("g")
                .attr("transform", `translate(${width - margin.right - 120}, ${margin.top})`);
    
            legend.selectAll("rect")
                .data(["Flat", "House"])
                .join("rect")
                .attr("x", 0)
                .attr("y", (d, i) => i * 20)
                .attr("width", 15)
                .attr("height", 15)
                .attr("fill", d => colorScale(d));
    
            legend.selectAll("text")
                .data(["Flat", "House"])
                .join("text")
                .attr("x", 20)
                .attr("y", (d, i) => i * 20 + 12)
                .text(d => d);
        }
    }, [rentComparisons]);
    
    



    return (
        <div className='prediction-container'>
            <h2>
                Generated rent predictions for a {numRooms} bedroom {houseType} in {normalizedSuburb} for the next {rentalPeriod} months
            </h2>
            <h3>Average rent per week would be {predictedPrice} AUD</h3>
            <div className='historical-price-chart'>
                <h4>Historical Rent Prices (last 6 months):</h4>
                <svg ref={svgRef}></svg>
            </div>
            <div className='rent-comparison-chart'>
                <h4>Rent Comparisons in Nearby Suburbs:</h4>
                <svg ref={comparisonSvgRef}></svg>
            </div>
        </div>
    );
};

export default GeneratedRentPrediction;
