import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './SalesPred.css';

function SalesPred() {
    const [suburb, setSuburb] = useState('');
    const [suggestions, setSuggestions] = useState([]);
    const [numRooms, setNumRooms] = useState('');
    const [houseType, setHouseType] = useState('');
    const [postcode, setPostcode] = useState(null); // New state to store postcode

    const handleSuburbChange = async (e) => {
        const userInput = e.target.value;
        setSuburb(userInput);

        if (userInput) {
            const response = await fetch(`http://localhost:5000/api/suburbs?query=${userInput}`);
            const data = await response.json();
            setSuggestions(data);
        } else {
            setSuggestions([]);
        }
    };

    const handleSuggestionClick = async (suggestion) => {
        setSuburb(suggestion);
        setSuggestions([]);

        // Fetch the postcode based on the selected suburb
        const response = await fetch(`http://localhost:5000/api/postcode?suburb=${suggestion}`);
        const data = await response.json();
        setPostcode(data.postcode); // Assuming the API returns { postcode: '1234' }
    };

    return (
        <div className="sales-prediction-container">
            <h1>View the sales price of the house you are looking for</h1>

            {/* Suburb Input */}
            <label className="input-label">
                Suburb: 
                <input 
                    type="text" 
                    value={suburb} 
                    onChange={handleSuburbChange} 
                    placeholder="Enter suburb"
                    autoComplete="off"
                    className="input-field"
                />
            </label>
            {suggestions.length > 0 && (
                <ul className="suggestions-list">
                    {suggestions.map((suggestion, index) => (
                        <li 
                            key={index}
                            onClick={() => handleSuggestionClick(suggestion)}
                            className="suggestion-item"
                        >
                            {suggestion}
                        </li>
                    ))}
                </ul>
            )}

            <label className="input-label">
                Number of rooms:
                <select 
                    value={numRooms} 
                    onChange={(e) => setNumRooms(e.target.value)}
                    className="input-field"
                >
                    <option value="">Select number of rooms</option>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                    <option value="6">6</option>
                    <option value="7">7</option>
                </select>
            </label>

            {/* Type of House Dropdown */}
            <label className="input-label">
                Type of house:
                <select 
                    value={houseType} 
                    onChange={(e) => setHouseType(e.target.value)}
                    className="input-field"
                >
                    <option value="">Select house type</option>
                    <option value="House">House</option>
                    <option value="Townhouse">Townhouse</option>
                    <option value="Unit">Unit</option>
                </select>
            </label>

            {/* Button to Navigate to Generated Sales Prediction */}
            <Link to={{
                pathname: "/GeneratedSalesPrediction",
                state: { 
                    postcode: postcode,   // Ensure postcode is passed
                    rooms: numRooms,
                    houseType: houseType
                }
            }}>
                <button className="generate-button" disabled={!postcode}>Generate Sales Prediction</button>
            </Link>
        </div>
    );
}

export default SalesPred;
