import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Polygon } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const center = [-37.8136, 144.9631]; // Melbourne's approximate center

function MarketAnalysis() {
    const [highSalesSuburbs, setHighSalesSuburbs] = useState([]);

    useEffect(() => {
        async function fetchData() {
            try {
                // Ensure the path is correct by placing the file in the `public` folder
                const response = await fetch('/highSalesSuburbData.json'); 
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                const suburbData = await response.json();
                console.log("Fetched suburb data:", suburbData); // Debugging: check if data is correctly fetched
                setHighSalesSuburbs(suburbData);
            } catch (error) {
                console.error("Failed to fetch suburb data:", error);
            }
        }
        fetchData();
    }, []);

    // Function to determine color intensity based on sales
    const getColor = (averagePrice) => {
        if (averagePrice > 1200000) return '#FF4500'; // High sales (bright red)
        if (averagePrice > 1000000) return '#FFA500'; // Medium-high sales (orange)
        if (averagePrice > 800000) return '#FFD700'; // Medium sales (yellow)
        return '#ADFF2F'; // Low sales (greenish-yellow)
    };

    return (
        <div style={{ height: '100vh', width: '100%' }}>
            <h1>Market Analysis</h1>
            <MapContainer center={center} zoom={10} style={{ height: '600px', width: '100%' }}>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
                />
                {highSalesSuburbs.map((suburb, index) => (
                    <Polygon
                        key={index}
                        positions={suburb.coordinates}
                        pathOptions={{
                            color: getColor(suburb.average_price),
                            fillColor: getColor(suburb.average_price),
                            fillOpacity: 0.6,
                            weight: 1
                        }}
                    />
                ))}
            </MapContainer>
        </div>
    );
}

export default MarketAnalysis;
